import { XyoBuffer } from '../../object-model'
import { XyoBoundWitness } from '../xyo-bound-witness'

describe('XyoBoundWitness', () => {
  it('Not completed', () => {
    // tslint:disable-next-line:max-line-length
    const bytes = Buffer.from(
      '',
      'hex'
    )
    const buffer = new XyoBuffer(bytes)
    const boundWitness = new XyoBoundWitness(buffer)

    expect(boundWitness.getIsCompleted()).toBe(false)
  })

  it('Is completed', () => {
    // tslint:disable-next-line:max-line-length
    const bytes = Buffer.from(
      '',
      'hex'
    )
    const buffer = new XyoBuffer(bytes)
    const boundWitness = new XyoBoundWitness(buffer)

    expect(boundWitness.getIsCompleted()).toBe(true)
  })

  it('Number of parties', () => {
    // tslint:disable-next-line:max-line-length
    const bytes = Buffer.from(
      '',
      'hex'
    )
    const buffer = new XyoBuffer(bytes)
    const boundWitness = new XyoBoundWitness(buffer)

    expect(boundWitness.getNumberOfFetters()).toBe(2)
    expect(boundWitness.getNumberOfWitnesses()).toBe(2)
    expect(boundWitness.getNumberOfParties()).toBe(2)
  })

  it('Get fetter', () => {
    // tslint:disable-next-line:max-line-length
    const fetter0Bytes = Buffer.from(
      '201547201944000C4192BAF8FBA41F6B5CA997DF7634F1F33176E0DDA8F7B485C6CD2EBC3BA06D4EEC8BB98284DB33761BA8A7668D1A5C140384968A0BE3436067F10A0D6B7F5AAFFF',
      'hex'
    )
    // tslint:disable-next-line:max-line-length
    const fetter1Bytes = Buffer.from(
      '201547201944000C41ED1512DA596726D8E19A592BBA5573D31174C424FDFD7A0D14B3088BD22F0EB520F99E19D78DBBD613B79277FEB2BD0911C4C379E69B8688CC744B5B5ACF928F',
      'hex'
    )
    // tslint:disable-next-line:max-line-length
    const bytes = Buffer.from(
      '',
      'hex'
    )
    const buffer = new XyoBuffer(bytes)
    const boundWitness = new XyoBoundWitness(buffer)

    const fetter0 = boundWitness
      .getFetterOfParty(0)
      ?.getAll()
      .getContentsCopy()
      .toString('hex')
    const fetter1 = boundWitness
      .getFetterOfParty(1)
      ?.getAll()
      .getContentsCopy()
      .toString('hex')

    expect(fetter0).toBe(fetter0Bytes.toString('hex'))
    expect(fetter1).toBe(fetter1Bytes.toString('hex'))
  })

  it('Get signing data', () => {
    // tslint:disable-next-line:max-line-length
    const signingData = Buffer.from(
      '201547201944000C4192BAF8FBA41F6B5CA997DF7634F1F33176E0DDA8F7B485C6CD2EBC3BA06D4EEC8BB98284DB33761BA8A7668D1A5C140384968A0BE3436067F10A0D6B7F5AAFFF201547201944000C41ED1512DA596726D8E19A592BBA5573D31174C424FDFD7A0D14B3088BD22F0EB520F99E19D78DBBD613B79277FEB2BD0911C4C379E69B8688CC744B5B5ACF928F',
      'hex'
    )
    // tslint:disable-next-line:max-line-length
    const bytes = Buffer.from(
      '',
      'hex'
    )
    const buffer = new XyoBuffer(bytes)
    const boundWitness = new XyoBoundWitness(buffer)

    const madeSigningData = boundWitness.getSigningData().toString('hex')

    expect(madeSigningData).toBe(signingData.toString('hex'))
  })
})
